const Home = () => {
  return (
    <div>
      <div className="posts">
        {posts.map(post =>(
            <div className="post" key={post.id}>
                <div className="img">
                <img src={post.img} alt="" />
                </div>
                <div className="content">
                    <div className="title">
                    <Link className='LinkP' to={`/post/${post.id}`}>
